const product_reducer = (state = {
    price: 100,
    product_name: "LAPTOP"
}, action) => {
    if (action.type == "add") {
        return state.price + 10
    }
    else if (action.type == "remove") {
        return state.price - 10
    }
    else if (action.type == "") {
        return state.price
    }
    else if (action.type == "") {
        return state.price
    }
    else {
        return state
    }
}

export default product_reducer
